//returns nearest point of interest and distance from it
//given the point coordinates (lat, lon)
function getNearestPoiAndDistance(lat, lon){
    //initialize POI as the first one and compute distance from it
    d = computeDistance(lat, lon, POI[0]);
    p = POI[0];
    //for each POI compute distance and update p 
    //if d1 is lower than current distance from closest 
    //start from POI[1] because 0 has already been considered
    for(j=1; j<POI.length; j++){
        console.log(POI[j].Name);
        d1 = computeDistance(lat, lon, POI[j]);
        if(d1<d){
            d = d1;
            p = POI[j];
        }
    }
    //return both POI and distance because needed for cleaning
    return [p,d];

}

//compute distance in meters from a given point of interest
function computeDistance(lat, lon, poi){
    return haversine(lat, lon, poi.lat, poi.lon);
}

/*---------------------------------------------
