//returns distance in meters between two points given 
//latitude and longitude, computing haversine function
//info about haversine: https://community.esri.com/t5/coordinate-reference-systems-blog/distance-on-a-sphere-the-haversine-formula/ba-p/902128
function haversine(lat1, lon1, lat2, lon2){
    R = 6371000;  // radius of Earth in meters
    //convert latitudes to radians
    phi1 = degToRad(lat1);
    console.log("Phi1: " + phi1);
    phi2 = degToRad(lat2);
    console.log("Phi2: " + phi2);
    //compute differences in latitudes and longitudes
    deltaPhi = degToRad(lat2 - lat1);
    deltaLambda = degToRad(lon2 - lon1);
    console.log("deltaPhi: " + deltaPhi);
    console.log("deltaLambda: " + deltaLambda);
    //actual computation of haversine
    a = Math.sin(deltaPhi / 2.0) ** 2 + Math.cos(phi1) * Math.cos(phi2) * Math.sin(deltaLambda / 2.0) ** 2;
    c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1 - a));
    meters = R * c;  // output distance in meters
    console.log("a: " + a);
    console.log("c: " + c);
    console.log("Meters: " + meters);
    km = meters / 1000.0  // output distance in kilometers
    console.log("Kilometers: " + km);
    console.log("----------------------------------------")
    return meters;
}

function degToRad(angle){
    while(angle > 360) angle -= 360;
    while(angle < -360) angle += 360;
    return angle * (Math.PI / 180);
}

