//receives as input the array of 6 measurements and computes the average
//returns the average if it is under a threshold, the array itself otherwise
function getNoisesOrAverage(noises){
    //create return array
    n = [];
    //initialize sum and count for average
    sum = 0;
    count = noises.length;
    //sum all noises
    noises.forEach(e => sum += e);
    //compute average
    avg = sum / count;
    //return results
    if(avg>context.threshold){
        return noises;
    }
    else{
        n.push(avg);
        return n;
    }
}

